(($)=>{$(document).ready(()=>{initMobileMenu()
initFaqAccordion()})
function initMobileMenu(){const menuToggle=$(".menu-toggle")
const primaryMenu=$(".primary-menu")
const menuClose=$(".menu-close")
menuToggle.on("click",function(e){e.preventDefault()
const isExpanded=$(this).attr("aria-expanded")==="true"
$(this).attr("aria-expanded",!isExpanded)
primaryMenu.toggleClass("is-open")
$(this).toggleClass("is-active")})
menuClose.on("click",function(){menuToggle.attr("aria-expanded","false")
primaryMenu.removeClass("is-open")
menuToggle.removeClass("is-active")})
$(document).on("click",(e)=>{if(!$(e.target).closest(".main-navigation").length){menuToggle.attr("aria-expanded","false")
primaryMenu.removeClass("is-open")
menuToggle.removeClass("is-active")}})
$(document).on("keydown",(e)=>{if(e.keyCode===27){menuToggle.attr("aria-expanded","false")
primaryMenu.removeClass("is-open")
menuToggle.removeClass("is-active")}})}
function initFaqAccordion(){$(".faq-question").on("click",function(){const faqItem=$(this).parent()
const faqAnswer=faqItem.find(".faq-answer")
if(faqItem.hasClass("open")){faqItem.removeClass("open")
faqAnswer.slideUp(300)}else{$(".faq-item").removeClass("open")
$(".faq-answer").slideUp(300)
faqItem.addClass("open")
faqAnswer.slideDown(300)}})}})(jQuery)